import random as rfrom uzwords import words as uwdef get_word():    r_w = r.choice(uw)    while "-" in r_w or " " in r_w:        r_w = r.choice(uw)    return r_w.upper()def display(user_letters , r_w):    display_letter = ""        for letter in r_w:        if letter in user_letters:            display_letter += letter        else:            display_letter += "-"    return display_letterdef play():    r_w = get_word()    word_letters = set(r_w)    user_letters = ""    print(f"Men {len(r_w)} honali so\'z o\'yladm topa olasizmi?")    while word_letters:        print(display(user_letters, r_w))        if user_letters:            print(f"Shu vaqtgacha kiritgan harflaringiz {user_letters}")        letter = input("Harf kiriting:").upper()        if letter in user_letters:            print("Bu harf ni oldin kiritgansiz boshqa harf kiriting")            continue        elif letter in r_w:            word_letters.remove(letter)            print(f"{letter} harfi to\'g\'ri")        else:            print("Bunday harf yo\'q")        user_letters += letter    print(f" Tabriklayman siz {r_w} so\'zini {len(user_letters)}ta urunishda topdingiz. ")            